  document.addEventListener("DOMContentLoaded", () => {
    const seriesTbody = document.getElementById("series-tbody")
    const detailSection = document.getElementById("series-detail") as HTMLElement
    const detailImage = document.getElementById("series-image") as HTMLImageElement
    const detailTitle = document.getElementById("series-title") as HTMLElement
    const detailDescription = document.getElementById("series-description") as HTMLElement
    const detailLink = document.getElementById("series-link") as HTMLAnchorElement
  
    function renderSeriesInTable(seriesList: Serie[]): void {
      if (!seriesTbody) {
        console.error("No se encontró el elemento series-tbody")
        return
      }
  
      seriesTbody.innerHTML = ""
  
      seriesList.forEach((serie) => {
        const tr = document.createElement("tr")
        tr.innerHTML = `
          <td>${serie.id}</td>
          <td>${serie.name}</td>
          <td>${serie.channel}</td>
          <td>${serie.seasons}</td>
        `
  
        tr.addEventListener("click", () => {
          showSeriesDetail(serie)
        })
  
        seriesTbody.appendChild(tr)
      })
  
      const avgSeasons = calculateAvgSeasons(seriesList)
      const avgElement = document.createElement("div")
      avgElement.className = "mt-3 alert alert-info"
      avgElement.textContent = `Promedio de temporadas: ${avgSeasons}`
  
      const container = document.querySelector(".container")
      if (container) {
        const existingAvg = document.querySelector(".alert-info")
        if (existingAvg) {
          container.removeChild(existingAvg)
        }
        container.appendChild(avgElement)
      }
    }
  
    function calculateAvgSeasons(seriesList: Serie[]): number {
      const total = seriesList.reduce((sum, serie) => sum + serie.seasons, 0)
      return Math.round((total / seriesList.length) * 10) / 10 // Redondear a 1 decimal
    }
  
    function showSeriesDetail(serie: Serie): void {
      detailSection.style.display = "block"
      detailImage.src = serie.image
      detailImage.alt = serie.name
      detailTitle.textContent = serie.name
      detailDescription.textContent = serie.description
      detailLink.href = serie.link
    }
  
    renderSeriesInTable(series)
  
    console.log("Series cargadas:", series.length)
  })
  